DisplayWaterTank=1;

//copy the flow to local variables for readbility
DisplayWaterTank--;
tankSerialNo = flow.get("freshwater["+DisplayWaterTank.toString(10)+"].SerialNo")

if ( !(msg.$source.includes(tankSerialNo) )){
    //this delta isn't for this guage.  Lets terminate the flow here.
    msg = {};
    node.done;
}
else
{
    //since we have the correct tank lets pull the information local to improve readabilit
    tankCapacity = flow.get("freshwater["+DisplayWaterTank.toString(10)+"].capacity")
    tankName = flow.get("freshwater["+DisplayWaterTank.toString(10)+"].Name")

    //set some local limits for colouring the guage different colours.  This translates is arbitary
    seg1b=0.05;
    seg2b=0.15; 

    //pull the level out of the tank from the data packet.
    tankCurrentLevel = msg.payload; //current level of the tank

    //label the guage with the name set up in the config run
    msg.guageName = tankName;

    //guages relative - if l confuse you ;-)  The guages only change when there is an update so it might take
    //5min to get the update
    if (global.get("Guages_Relative") == true)
    {
        msg.payload = (tankCurrentLevel*100).toFixed(0);
        msg.unit="%";
        guage_min = 0
        guage_max = 100
        seg1= seg1b*guage_max;
        seg2= seg2b*guage_max;
    }
    else
    {
        //guage absolute the capacity is converted to l,  this is where you gould pick another unit.

        //conversion=1; //you like cubig meters
        conversion=1000; //liters in a cubic meter
        //conversion=264.17; //us gallons in a cubic meter 

        msg.payload = (tankCurrentLevel*tankCapacity*conversion).toFixed(0);
        //msg.unit="m^3";
        msg.unit="l";
        //msg.unit="US Gal";
        guage_min = 0
        guage_max = (tankCapacity*conversion).toFixed(0)
        seg1= seg1b* guage_max;
        seg2= seg2b*guage_max;
    }

    //set th
    msg.ui_control = {
        "min":guage_min,
        "max":guage_max,
        "seg1":seg1,
        "seg2":seg2,
        "options":{"levelColors":["#b30000", "#e6e600", "#59cb3a"]}
    }

}


return msg;

