led_state = false; // false makes the LED red
reading_bound = 300000; //in ms , this sets the tollerance AND lockout for bad readings.

//the time of the reading
readingTime = flow.get("locals.lastGoodReading");
//the last bad reading
lastBadReading = new Date(flow.get("locals.lastBadReading")).getTime();

//the present time acording to the node red server
//this may drift unless all your devices are time locked. THEY ARE of course time locked.
currentTime =  Date.now();

//work out the time deltas
timeFromLastBadReading = lastBadReading - currentTime;
timeFromLastMeasurement = readingTime - currentTime;

if ( timeFromLastBadReading <= -reading_bound){

     if ( timeFromLastMeasurement <= -reading_bound){
         //we have a reading time out
        led_state = false;
        flow.set("locals.lastBadReading", currentTime);
    }
    else
    {
        //all is good set the led green
        led_state = true;
    }
}
else
{
   //we have seen a reading gap leave the LED on red
    led_state = false;
}

//set the outputs

msg.payload = led_state;
//for debug I output the time deltaa
